import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Typography from '@mui/material/Typography';
import { BsArrowRightShort } from 'react-icons/bs';
import SplitButton from './splitButton';
import { Box, Link, Stack } from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

const CardLabel = styled(Typography)(({ theme }) => ({
  fontSize: '1.2rem',
  fontWeight: 'bold',
  color: theme.customProps.color,
  marginBottom: '0.5rem',
}));

const CardLink = styled(Link)(({ theme }) => ({
  fontSize: '.7rem',
  textDecoration: 'underline',
  color: theme.palette.primary.main,
  marginRight: '1.5rem',
}));

/**
 *
 * @param param0
 * @returns
 */
const CardsContainer = ({
  children,
  cardLable,
  Icon,
  hasDate,
  componentHeight,
  hasReadMoreArrow,
  backgroundColor,
  readMoreLink,
  image,
  LabelColor,
  hasTopRightUrl,
}) => {
  const styles = {
    card: {
      maxWidth: '100%',
      minHeight: componentHeight,
      marginBottom: 2,
      backgroundColor: backgroundColor,
      borderRadius: '10px',
    },
    stackOne: {
      display: 'flex',
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '1rem',
    },
    typoOne: {
      fontSize: '1rem',
      color: '#103B66',
      fontWeight: '400',
    },
    link: { color: '#21B8F9', textDecoration: 'underline', px: [3] },
    stackTwo: {
      marginRight: '0rem',
      marginBottom: '1rem',
      width: '30%',
    },
    cardLabel: { color: LabelColor ? LabelColor : '' },
    typoFour: {
      fontSize: '1rem',
      color: '#103B66',
      fontWeight: '700',
    },
    cardActions: {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'flex-start',
      alignItems: 'center',
      mx: 2,
    },
    box: {
      py: [2],
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      color: '#21B8F9',
    },
  };
  return (
    <Card sx={styles.card}>
      <CardContent>
        <Stack sx={styles.stackOne}>
          <Stack direction='row' columnGap={1}>
            {image && <img src={image} alt='start' style={styles.stackTwo} />}
            {Icon}
            <CardLabel variant='body2' style={styles.cardLabel}>
              {cardLable}
            </CardLabel>
          </Stack>
          {hasDate && <SplitButton />}
          {hasTopRightUrl && (
            <Box sx={styles.box}>
              <Link href='#' sx={styles.link}>
                visit our blog
              </Link>
              <OpenInNewIcon />
            </Box>
          )}
        </Stack>
        {children}
      </CardContent>
      <CardActions sx={styles.cardActions}>
        <CardLink>{readMoreLink}</CardLink>
        {hasReadMoreArrow && <BsArrowRightShort size={25} color='#21B8F9' />}
      </CardActions>
    </Card>
  );
};

export default CardsContainer;
