/** @jsx jsx */
import React from 'react';
import { jsx, Box, Text, Container, Heading, Input, Button } from 'theme-ui';
import { Link } from 'components/link';
import Logo from 'components/logo';
import logo from 'assets/logo.svg';
import menuItems from './footer.data';

export default function Footer() {
  return (
    <footer sx={styles.footer}>
      <Container>
        <Box>
          <Logo image={logo} />
        </Box>
      </Container>
      <Container sx={styles.footer.container}>
        <Box as='section' sx={styles.subscribe}>
          <Container>
            <Heading as='h3'>Subscribe to get notified about event</Heading>
            <Text as='p'>By subscribing with your mail, you will accept our privacy policy</Text>
            <Box as='form' sx={styles.form}>
              <Box as='label' htmlFor='subscribeEmail' variant='styles.srOnly'>
                Email
              </Box>
              <Input
                placeholder='Enter your email'
                type='email'
                id='subscribeEmail'
                sx={styles.input}
              />
              <Button type='submit' sx={styles.button}>
                Subscribe us
              </Button>
            </Box>
          </Container>
        </Box>
        <Box sx={styles.footer.footerTopArea}>
          {menuItems.map(({ header, items }, i) => (
            <Box sx={styles.footer.menus} key={i}>
              <Heading sx={styles.footer.heading}>{header}</Heading>
              <nav>
                {items.map(({ path, label }, i) => (
                  <Link path={path} key={i} label={label} sx={styles.footer.link} />
                ))}
              </nav>
            </Box>
          ))}
        </Box>
      </Container>
      <Text sx={styles.footer.copyright}>All right reserved</Text>
    </footer>
  );
}


const styles = {
  footer: {
    container: {
      width: '100%',
      alignItems: 'stretch',
      display: 'flex',
    },
    footerTopArea: {
      borderTop: '1px solid',
      borderTopColor: 'border_color',
      display: 'flex',
      flexWrap: 'wrap',
      bg: 'blue'
    },
    menus: {
      width: ['50%', null, null, '25%'],
      display: 'flex',
      flexDirection: 'column',
      bg: 'black'
    },

    heading: {
      fontSize: [3, null, null, 4],
      color: 'text_secondary',
      fontWeight: '500',
      mb: [3, 4, 5, null, 6],
      lineHeight: '1.35',
    },

    link: {
      fontSize: ['14px', null, 1],
      color: 'text',
      fontWeight: 'body',
      mb: 2,
      cursor: 'pointer',
      transition: 'all 0.35s',
      display: 'block',
      textDecoration: 'none',
      lineHeight: [1.5, null, null, 1.6, 1.8],
      ':hover': {
        color: 'primary',
      },
      ':last-child': {
        mb: '0px',
      },
    },
    copyright: {
      fontSize: ['14px', null, 1],
      width: '100%',
      textAlign: 'center',
      p: ['20px 20px'],
      backgroundColor: '#FCFDFE',
      color: 'text',
      a: {
        textDecoration: 'none',
        color: 'inherit',
        pl: 1,
        transition: 'all 0.25s',
        '&:hover': {
          color: 'primary',
        },
      },
    },
  },
};
