/** @jsx jsx */
import React from 'react';
import { jsx, Box, Text, Container, Heading } from 'theme-ui';
import { Link } from 'components/link';
import Logo from 'components/logo';
import logo from 'assets/logo.svg';
import menuItems from './footer.data';

export default function Footer() {
  return (
    // <Box as='footer' sx={styles.footer}>
    //   <Container sx={styles.container}>
    //     <Box
    //       sx={{
    //         display: 'flex',
    //         alignItems: 'center',
    //         flexDirection: ['column', null, null, null, null, 'row'],
    //       }}
    //     >
    //       <Logo image={logo} />
    //     </Box>
    //   </Container>
    //   <Container sx={styles.footer.container}>
    //     <Box sx={styles.footer.footerTopArea}>
    //       {menuItems.map(({ header, items }, i) => (
    //         <Box sx={styles.footer.menus} key={i}>
    //           <Heading sx={styles.footer.heading}>{header}</Heading>
    //           <nav>
    //             {items.map(({ path, label }, i) => (
    //               <Link path={path} key={i} label={label} sx={styles.footer.link} />
    //             ))}
    //           </nav>
    //         </Box>
    //       ))}
    //     </Box>
    //   </Container>
    // </Box>

    <footer sx={styles.footer}>
      <Container sx={styles.footer.container}>
        <Box sx={styles.footer.footerTopArea}>
          {menuItems.map(({ header, items }, i) => (
            <Box sx={styles.footer.menus} key={i}>
              <Heading sx={styles.footer.heading}>{header}</Heading>
              <nav>
                {items.map(({ path, label }, i) => (
                  <Link
                    path={path}
                    key={i}
                    label={label}
                    sx={styles.footer.link}
                  />
                ))}
              </nav>
            </Box>
          ))}
        </Box>
      </Container>
      <Text sx={styles.footer.copyright}>
        All right reserved - Design & Developed by
        <Link path="https://redq.io/" target="_blank">
          RedQ, Inc
        </Link>
      </Text>
    </footer>
  );
}

const styles = {
  footer: {
    py: ['40px', null, null, null, null, '30px', '40px'],
    backgroundColor: '#020718',
    position: 'relative',
    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: '#ffffff',
      opacity: '0.05',
    },
  },
  container: {
    display: 'flex',
    flexDirection: ['column', null, null, null, null, 'row'],
    justifyContent: ['center', null, null, null, null, 'space-between'],
    alignItems: 'center',
    position: 'relative',
    flexWrap: 'wrap',
  },
  linksWrap: {
    mt: ['15px', null, null, null, null, '0'],
    display: 'flex',
    flexWrap: 'wrap',
    a: {
      fontSize: ['14px', null, null, null, '16px'],
      color: '#ffffff',
      transition: 'all 500ms ease',
      '&:hover': { opacity: 0.7 },
    },
    'a+a': { ml: ['15px', null, null, null, '35px'] },
  },
};
